import React, {Component} from 'react'

class RecepieForm extends Component{
	constructor(props){
		super(props);
		this.state = {
			title: '',
			ingredients: [],
			instructions: '',
			img: ''
		}

		this.updateTitle = (e) => {
			this.setState({...this.state, title: e.target.value});
		}
	}
	render(){
		return(
			<div>
				<form>
					<button>X</button>
					<label for= 'title'>Title</label>
					<input
						type= 'text'
						onChange = {this.updateTitle}
						id= 'title'
						/>
				</form>
			</div>
		);
	}
}

export default RecepieForm;
