import React, {Component} from 'react'
import './recepie_form.css'

class RecepieForm extends Component{
	constructor(props){
		super(props);
		this.state = {
			title: '',
			ingredients: [''],
			instructions: '',
			img: ''
		}

		this.updateTitle = (e) => {
			this.setState({...this.state, title: e.target.value});
		}
		this.updateImgUrl = (e) => {
			this.setState({...this.state, img: e.target.value});
		}
		this.addIngredient = (e) =>{
			var newIngredients = this.state.ingredients.slice().push(e.target.value);
			this.setState(({...this.state, ingredients: newIngredients}));
		}
	}
	render(){
		var ingredients = this.state.ingredients.map((value, index)=>{
			return(
				<div className = 'InputDiv'>
					<label for = {index}>{`${index+1}.`}</label>
					<input
						id = {index}
						type = 'text'
						value = {value}
						autocomplete = 'false'
						placeholder= 'Ingredient'/>
				</div>
			);
		});
		return(
			<div className = 'RecepieForm'>
				<div className = 'ButtonDiv'>
					<button className='CloseButton'>X</button>
				</div>
				<form>
					<div className = 'InputDiv'>
						<label for= 'title'>Title</label>
						<input
							type= 'text'
							onChange = {this.updateTitle}
							id= 'title'
							/>
					</div>
					<div>
						<label for= 'instructions'>Instructions</label>
						<div className = 'InputDiv'>
							<textarea
								className = 'InstructionsInput'
								rows = '3'
								id = 'instructions'
								placeholder= 'Add instructions'
								/>
						</div>
					</div>
					<div>{ingredients}</div>
					<div className = 'ButtonDiv'>
						<button className='BlueButton' onClick={this.addIngredient}>+</button>
					</div>
					<div className = 'InputDiv'>
					<label for= 'imgUrl'>Image URL</label>
						<input
							type= 'text'
							onChange = {this.imgUrl}
							id= 'imgUrl'
							/>
					</div>
					<div className = 'ButtonDiv'>
						<button className='BlueButton'>Save</button>
					</div>
				</form>
			</div>
		);
	}
}

export default RecepieForm;
