import React, {Component} from 'react'
import './recepie_form.css'

class RecepieForm extends Component{
	constructor(props){
		super(props);
		this.state = {
			title: '',
			ingredients: [''],
			instructions: '',
			img: ''
		}

		this.updateChanges = (e) => {
			this.setState({...this.state, [e.target.id]: e.target.value});
		}
		this.updateIngredients = (e) =>{
			var newIngredients = this.state.ingredients.slice();
			newIngredients[this.state.ingredients.length-1] = e.target.value;
			this.setState(({...this.state, ingredients: newIngredients}));
		}
		this.addIngredient = (e) =>{
			var newIngredients = this.state.ingredients.slice();
			newIngredients.push('');
			this.setState(({...this.state, ingredients: newIngredients}));
		}
	}
	render(){
		var ingredients = this.state.ingredients.map((value, index)=>{
			return(
				<div className = 'InputDiv' key = {index}>
					<label htmlFor = {index}>{`${index+1}.`}</label>
					<input
						type = 'text'
						value = {value}
						placeholder= 'Ingredient'
						onChange = {this.updateIngredients}/>
				</div>
			);
		});
		return(
			<div className = 'RecepieForm'>
				<div className = 'ButtonDiv'>
					<button className='CloseButton'>X</button>
				</div>
				<form className = 'Form'>
					<div className = 'InputDiv'>
						<label htmlFor= 'title'>Title</label>
						<input
							type= 'text'
							onChange = {this.updateChanges}
							id = 'title'
							/>
					</div>
					<div>
						<label htmlFor= 'instructions'>Instructions</label>
						<div className = 'InputDiv'>
							<textarea
								className = 'InstructionsInput'
								rows = '3'
								id = 'instructions'
								placeholder= 'Add instructions'
								onChange = {this.updateChanges}
								/>
						</div>
					</div>
					<div>{ingredients}</div>

						<button 
							className='BlueButton'
							onClick={this.addIngredient}
							type = 'button'
						>
							+
						</button>
					<div className = 'InputDiv'>
					<label htmlFor= 'imgUrl'>Image URL</label>
						<input
							type= 'text'
							onChange = {this.imgUrl}
							id= 'imgUrl'
							/>
					</div>
					<div className = 'ButtonDiv'>
						<button className='BlueButton'>Save</button>
					</div>
				</form>
			</div>
		);
	}
}

export default RecepieForm;
