import React, {Component} from 'react'

class RecepieCard extends Component{
	render(){
		const {title, ingredients} = this.props;
		var ingrList = ingredients.map((value,index) => (<li key = {index}>{value}</li>));
		return(
			<div>
				<h2>{title}</h2>
				<ul>
					{ingrList}
				</ul>
			</div>
		);
	}
}

export default RecepieCard;
