import React, {Component} from 'react';
import logo from './logo.svg';
import './App.css';
import NavBar from './NavBar'
import RecepieList from './RecepieList'
import RecepieForm from './RecepieForm'

class App extends Component {	
	constructor(props){
		super(props);
		this.state = {
			recepies: [
				{
					id: 1,
					title: 'pasta',
					ingredients: ['flour','water'],
					instructions: "Mix all the stuff",
					img: 'pasta.jpg'
				},
				{
					id: 2,
					title: 'maggie',
					ingredients: ['maggie','massala', 'vegetables', 'water', 'salt'],
					instructions: "Mix all the stuff",
					img: 'maggie.jpg'	
				},
				{
					id: 3,
					title: 'tea',
					ingredients: ['tea leaves','water', 'sugar', 'milk'],
					instructions: "Mix all the stuff",
					img: 'tea.jpg'
				}
			],
			nextRecepieId: 3
		}
	}

	static defaultProps = {
		navButtons: ['New Recepie', 'Home', 'About', 'Contact Us']
	}

	render(){
	  return (
		<div className="App">
			  <NavBar buttons = {this.props.navButtons}/>
			  <RecepieList recepieList = {this.state.recepies}/>
			  <RecepieForm/>
		</div>
	  );
	}
}

export default App;
